INSERT INTO videos(vid, pid, title, description) VALUES('Ks_nmYLExOU', 'PL-J5pP_ptFRCoKzTU1Im7J64sNMqiZE2V', '《楞嚴經》講要－卷一16：妄識無體(一)', '這一段經文阿難還是以凡夫的立場，來探討心的功能以及心的作用，而佛則是不斷地誘導阿難尊者從佛的角度來探討心是什麼模樣的。凡夫與佛所見的有何不同？且聽世尊的解答。');
