INSERT INTO playlist(pid, cid, title, description) VALUES('PL5UAW47GPT37twxeX_l-iXZZF8eNR3fpT','UCwDBO96zk6pgXsRjpRv--uw', '人生三大課題--全', '對生命本質的疑惑，是從古至今的人們都不曾停歇的探索。生從何來?死從何去?應作何事? 是值得深思的三個重要問題。\n\n本課程是擷自辜琮瑜老師於農禪寺講授關於人生課題的精要引領大家從不同的面向去了解生死，透析最深層的問題。\n\n問號的解答在哪裡? 請您自己去想一想!');
