INSERT INTO videos(vid, pid, title, description) VALUES('SVD6JeNDW6Q', 'PL7F3C78A0B4CA463A', '禮儀環保（聖嚴法師-大法鼓 0049)', '什麼是禮儀環保？\n為什麼禮儀也要環保呢？\n師父為什麼大力提倡禮儀環保？\n\n讓我們來請教聖嚴法師。\n更多線上課程：http://www.dharmaschool.com/index.html\n，，，，，，，，\n，');
